import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import abi from "./Raffle.json"; // 确保你有 ABI 文件

const RaffleApp = () => {
    const [connectedAccount, setConnectedAccount] = useState("");
    const [entranceFee, setEntranceFee] = useState("Loading...");
    const [contractBalance, setContractBalance] = useState("Loading...");
    const [recentWinner, setRecentWinner] = useState("None yet");
    const [raffleState, setRaffleState] = useState("Loading...");
    const [players, setPlayers] = useState([]);
    const [loading, setLoading] = useState(false);

    const raffleContractAddress = 0x3e670fEd42c2eEdD6d2cfC10e03f8e5840b4865F; // 替换成你的合约地址
    const contractAbi = abi; // 从你的 ABI 文件中获取

    useEffect(() => {
        const fetchContractData = async () => {
            try {
                if (typeof window.ethereum !== "undefined") {
                    const provider = new ethers.providers.Web3Provider(window.ethereum);
                    const signer = provider.getSigner();
                    const raffleContract = new ethers.Contract(
                        raffleContractAddress,
                        contractAbi,
                        signer
                    );

                    const account = await signer.getAddress();
                    setConnectedAccount(account);

                    // 获取Entrance Fee
                    const entranceFee = await raffleContract.getEntranceFee();
                    setEntranceFee(ethers.utils.formatEther(entranceFee));

                    // 获取Contract Balance
                    const contractBalance = await provider.getBalance(raffleContractAddress);
                    setContractBalance(ethers.utils.formatEther(contractBalance));

                    // 获取最近赢家
                    const recentWinner = await raffleContract.getRecentWinner();
                    setRecentWinner(recentWinner);

                    // 获取Raffle State
                    const raffleState = await raffleContract.getRaffleState();
                    setRaffleState(raffleState === 0 ? "Open" : "Calculating");

                    // 获取玩家数量
                    const playersArray = [];
                    const playerCount = await raffleContract.getLengthOfPlayers();
                    for (let i = 0; i < playerCount; i++) {
                        const player = await raffleContract.getPlayer(i);
                        playersArray.push(player);
                    }
                    setPlayers(playersArray);
                }
            } catch (error) {
                console.error("Error fetching contract data:", error);
            }
        };

        fetchContractData();
    }, []);

    const enterRaffle = async () => {
        setLoading(true);
        try {
            if (typeof window.ethereum !== "undefined") {
                const provider = new ethers.providers.Web3Provider(window.ethereum);
                const signer = provider.getSigner();
                const raffleContract = new ethers.Contract(
                    raffleContractAddress,
                    contractAbi,
                    signer
                );

                const entranceFee = await raffleContract.getEntranceFee();
                const tx = await raffleContract.enterRaffle({ value: entranceFee });
                await tx.wait();
                alert("Successfully entered the raffle!");
            }
        } catch (error) {
            console.error("Error entering the raffle:", error);
            alert("Failed to enter the raffle. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h1>Decentralized Raffle DApp</h1>
            <p>Connected Account: {connectedAccount}</p>

            <h2>Raffle Details</h2>
            <p>Entrance Fee: {entranceFee} ETH</p>
            <p>Contract Balance: {contractBalance} ETH</p>
            <p>Recent Winner: {recentWinner}</p>
            <p>Raffle State: {raffleState}</p>
            <p>Players:</p>
            <ul>
                {players.length === 0 ? (
                    <p>No players yet</p>
                ) : (
                    players.map((player, index) => <li key={index}>{player}</li>)
                )}
            </ul>

            <button onClick={enterRaffle} disabled={loading}>
                {loading ? "Entering..." : "Enter the Raffle"}
            </button>
        </div>
    );
};

export default RaffleApp;

