import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import './App.css';
import RaffleABI from './RaffleABI.json'; // 导入你的合约 ABI

const raffleAddress = 0x3e670fEd42c2eEdD6d2cfC10e03f8e5840b4865F; // 请替换为你的合约地址

function App() {
    const [account, setAccount] = useState(null);
    const [entranceFee, setEntranceFee] = useState("Loading...");
    const [contractBalance, setContractBalance] = useState("Loading...");
    const [recentWinner, setRecentWinner] = useState("None yet");
    const [raffleState, setRaffleState] = useState("Loading...");
    const [players, setPlayers] = useState([]);

    const connectWallet = async () => {
        if (window.ethereum) {
            const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
            setAccount(accounts[0]);
        } else {
            alert("Please install Metamask!");
        }
    };

    const loadContractData = async () => {
        if (!window.ethereum) return;

        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const contract = new ethers.Contract(raffleAddress, RaffleABI, provider);

        try {
            // 获取 Entrance Fee
            const fee = await contract.getEntranceFee();
            setEntranceFee(ethers.utils.formatEther(fee));

            // 获取合约余额
            const balance = await provider.getBalance(raffleAddress);
            setContractBalance(ethers.utils.formatEther(balance));

            // 获取最近赢家
            const winner = await contract.getRecentWinner();
            setRecentWinner(winner);

            // 获取抽奖状态
            const state = await contract.getRaffleState();
            setRaffleState(state === 0 ? "Open" : "Calculating");

            // 获取玩家数量
            const numPlayers = await contract.getLengthOfPlayers();
            let playerList = [];
            for (let i = 0; i < numPlayers; i++) {
                const player = await contract.getPlayer(i);
                playerList.push(player);
            }
            setPlayers(playerList);
        } catch (error) {
            console.error("Error loading contract data:", error);
        }
    };

    const enterRaffle = async () => {
        if (!window.ethereum) {
            alert("Please install Metamask!");
            return;
        }

        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(raffleAddress, RaffleABI, signer);

        try {
            const fee = await contract.getEntranceFee();
            const transaction = await contract.enterRaffle({ value: fee });
            await transaction.wait();
            alert("You have successfully entered the raffle!");
        } catch (error) {
            console.error("Error entering the raffle:", error);
        }
    };

    useEffect(() => {
        connectWallet();
        loadContractData();
    }, []);

    return (
        <div className="App">
            <h1>Decentralized Raffle DApp</h1>
            <p>Connected Account: {account}</p>
            <h2>Raffle Details</h2>
            <p>Entrance Fee: {entranceFee} ETH</p>
            <p>Contract Balance: {contractBalance} ETH</p>
            <p>Recent Winner: {recentWinner}</p>
            <p>Raffle State: {raffleState}</p>
            <p>Players:</p>
            <ul>
                {players.length === 0 ? <li>No players yet</li> : players.map((player, index) => <li key={index}>{player}</li>)}
            </ul>
            <button onClick={enterRaffle}>Enter Raffle</button>
        </div>
    );
}

export default App;

